package b5_10951;
import java.util.Scanner;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner s = new Scanner(System.in);
		int a, b;
		
		while(s.hasNext())
		{
			a = s.nextInt();
			b = s.nextInt();
			System.out.println(a+b);
		}
	}

}
